import osimport maya.cmds as cmdsimport maya.mel as mmimport shelvescmds.evalDeferred('shelves.create()')cmds.evalDeferred("""mm.eval('source "%s"' % (os.getenv("MAYA_CGRU_LOCATION") + "/cgru.mel"))""")def open_scene():    filename = cmds.file(q=True, sn=True)    if "/alpha/" in filename:        filepath = [x for x in filename.split("/") if x]        compile_filepath = "//" + "/".join(filepath[:3])        cmds.workspace(compile_filepath, openWorkspace=True)job = cmds.scriptJob(event=["SceneOpened", open_scene])