import osimport maya.cmds as cmdsimport maya.mel as mmimport shelvescmds.evalDeferred('shelves.create()')# Install CGRU Menucmds.evalDeferred("""mm.eval('source "%s"' % (os.getenv("MAYA_CGRU_LOCATION") + "/cgru.mel"))""")# Install Studio Libraryinstall_path = str(Path(os.path.dirname(__file__)).parent.parent.parent) + "/Studio Library 2.9.6.b3/install.mel"cmds.evalDeferred("""mm.eval('source "%s"'""" % install_path)def open_scene():    filename = cmds.file(q=True, sn=True)    if "/alpha/" in filename:        filepath = [x for x in filename.split("/") if x]        compile_filepath = "//" + "/".join(filepath[:3])        cmds.workspace(compile_filepath, openWorkspace=True)job = cmds.scriptJob(event=["SceneOpened", open_scene])