import maya.cmds as cmdsimport shelvescmds.evalDeferred('shelves.create()')def open_scene():    filename = cmds.file(q=True, sn=True)    if "Z:" in filename or "X:" in filename:        cmds.workspace("/".join(filename.split("/")[:2]), openWorkspace=True)job = cmds.scriptJob(event=["SceneOpened", open_scene])