import osimport maya.cmds as cmdsimport maya.mel as mmimport shelvesdef open_scene():    filename = cmds.file(q=True, sn=True)    if "/alpha/" in filename:        filepath = [x for x in filename.split("/") if x]        compile_filepath = "//" + "/".join(filepath[:3])        cmds.workspace(compile_filepath, openWorkspace=True)        print("[It's alive] Set project to " + compile_filepath)def open_ms_plugins():    cmds.loadPlugin('MayaUE4RBFPlugin2022')    cmds.loadPlugin('MayaUERBFPlugin')    cmds.loadPlugin('embeddedRL4')# Install shelvescmds.evalDeferred('shelves.create()')# Install CGRU Menucmds.evalDeferred("""mm.eval('source "%s"' % (os.getenv("MAYA_CGRU_LOCATION") + "/cgru.mel"))""")# Set project workspacejob = cmds.scriptJob(event=["SceneOpened", open_scene])# Open pluginscmds.evalDeferred("open_ms_plugins()")